mapper.insert:
insert into tbl_tran(
id, owner, money, name, expectedDate, customerId, stage, type, source, activityId, contactsId, createBy, createTime, description, contactSummary, nextContactTime
)
values(
#{id}, #{owner}, #{money}, #{name}, #{expectedDate}, #{customerId}, #{stage}, #{type}, #{source}, #{activityId}, #{contactsId}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime}
)

mapper.select:
select t.id, t.owner, t.money, t.name, t.expectedDate, u.name as customerId, t.stage, t.type, t.source, t.activityId, o.fullname as contactsId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime
from tbl_tran t join tbl_contacts o on t.contactsId=o.id join tbl_customer u on t.customerId=u.id
order by t.createTime desc limit #{skipCount},#{pageSize}

select t.id, t.owner, t.money, t.name, t.expectedDate, u.name as customerId, t.stage, t.type, t.source, t.activityId, o.fullname as contactsId, t.createBy, t.createTime, t.editBy, t.editTime, t.description, t.contactSummary, t.nextContactTime
from tbl_tran t join tbl_customer u on t.customerId=u.id join tbl_contacts o on t.contactsId=o.id
where t.id=#{id}
